import { useContractReader } from "eth-hooks";
import { ethers } from "ethers";
import React from "react";

import { Typography, Card, Button, List, Image } from "antd";
import { Link } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import { useEffect } from "react";

const { Title } = Typography;

function Home({ baseURL, userSigner, address, readContracts, writeContracts, tx }) {
  const [currentUserData, setCurrentUserData] = useState({ address: "", msgHash: "", msgSignature: "" });
  const [nfts, setNfts] = useState([1, 2, 3]);
  console.log("n-nfts: ", nfts);

  const onMint = async () => {
    let yourNFT = writeContracts["YourNFT"];
    // let tx = await yourNFT.whitelistMint(
    //   currentUserData.msgHash,
    //   currentUserData.msgSignature,
    //   `https://cataas.com/cat/cute`,
    // );
    // let rcpt = await tx.wait();
    // console.log("rcpt: ", rcpt);
    let rcpt = await tx(
      yourNFT.whitelistMint(currentUserData.msgHash, currentUserData.msgSignature, `https://cataas.com/cat/cute`),
    );

    console.log("rcpt: ", rcpt);
    updateList();
  };

  const findAddress = async () => {
    let response = await axios.post(`${ baseURL }/findAddress`, { address });
    response = response.data;
    if (response["userData"] !== undefined) {
      setCurrentUserData({ ...response["userData"] });
    }
    updateList();
  };

  const updateList = async () => {
    if ("YourNFT" in writeContracts) {
      let yourNFT = writeContracts["YourNFT"];
      let balance = await yourNFT.balanceOf(address);
      let tokenCount = +balance.toString();
      console.log("n-tokenCount: ", tokenCount);
      if (tokenCount !== 0) {
        setNfts([...Array(3 - tokenCount).keys()]);
      }
    }
  };

  useEffect(() => {
    if (address) {
      void findAddress();
    }
  }, [address, writeContracts]);

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Title level={2}>Cute cat's nft</Title>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {nfts.map(index => {
          return (
            <div key={index}>
              <Card
                title={index + 1}
                bordered={true}
                style={{
                  // width: "410%",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                  justifyContent: "center",
                }}
              >
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    justifyContent: "center",
                  }}
                >
                  <Image
                    width={250}
                    height={250}
                    src={
                      index === 1
                        ? `https://cataas.com/cat/cute`
                        : index === 2
                          ? `https://cataas.com/cat`
                          : `https://cataas.com/cat/cute/says/hello`
                    }
                    fallback="data:image/png;base64,"
                  />
                  <Button
                    type="primary"
                    style={{ margin: 10 }}
                    onClick={onMint}
                    disabled={currentUserData["msgHash"] === ""}
                  >
                    mint a cutiee
                  </Button>
                </div>
              </Card>
            </div>
          );
        })}

        {nfts.length === 0 && (
          <Title level={3} type="warning">
            You minted all your NFT's
          </Title>
        )}
      </div>
    </div>
  );
}

export default Home;
